import React, { useState } from 'react';
import { Link, useHistory } from 'react-router-dom';

const CrearTareas = () => {
  const [nombreTarea, setNombreTarea] = useState('');
  const [descripcionTarea, setDescripcionTarea] = useState('');
  const history = useHistory();

  const handleNombreChange = (event) => {
    setNombreTarea(event.target.value);
  };

  const handleDescripcionChange = (event) => {
    setDescripcionTarea(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Aquí puedes realizar acciones con los datos de la tarea, como enviarlos al servidor o almacenarlos en el estado global de la aplicación.
    const nuevaTarea = {
      nombre: nombreTarea,
      descripcion: descripcionTarea,
    };

    // Lógica para agregar la tarea a una lista de tareas existente
    // ...

    // Redirigir al usuario a la lista de tareas o a otra vista después de crear la tarea
    history.push('/'); // Por ejemplo, redirige a la lista de tareas

    // También puedes mostrar una notificación de éxito o realizar otras acciones necesarias
  };

  return (
    <div>
      <h1>Crear Nueva Tarea</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="nombreTarea">Nombre de la Tarea:</label>
          <input
            type="text"
            id="nombreTarea"
            value={nombreTarea}
            onChange={handleNombreChange}
          />
        </div>
        <div>
          <label htmlFor="descripcionTarea">Descripción de la Tarea:</label>
          <textarea
            id="descripcionTarea"
            value={descripcionTarea}
            onChange={handleDescripcionChange}
          ></textarea>
        </div>
        <button type="submit">Crear Tarea</button>
      </form>
      <Link to="/">Volver a la Lista de Tareas</Link>
    </div>
  );
};

export default CrearTareas;
