import React, { useState } from 'react';

const CrearTareas = () => {
  const [nombreTarea, setNombreTarea] = useState('');

  const handleInputChange = (event) => {
    setNombreTarea(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    if (nombreTarea.trim() === '') {
      alert('Por favor, ingresa un nombre para la tarea.');
      return;
    }

    // Realiza alguna acción con el nombre de la tarea aquí,
    // como enviarlo a un servidor o almacenarlo en otro componente.

    console.log('Nombre de la tarea:', nombreTarea);

    // Limpia el campo de entrada después de crear la tarea
    setNombreTarea('');
  };

  return (
    <div>
      <h1>Crear Nueva Tarea</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Nombre de la tarea:
          <input
            type="text"
            value={nombreTarea}
            onChange={handleInputChange}
          />
        </label>
        <button type="submit">Crear Tarea</button>
      </form>
    </div>
  );
};

export default CrearTareas;
